import React from 'react';
import PropTypes from 'prop-types';
import lifeExpectancies from '../api/lifeExpectancies';
import { CSSTransitionGroup } from 'react-transition-group';
import moment from 'moment';

function Result(props) {


  const ageAtDeath = props.dateOfDeath.diff(props.birthday, 'years');
  const yearsFromNow = moment().diff(props.dateOfDeath,'years');
  return (
    <CSSTransitionGroup
      className="container result"
      component="div"
      transitionName="fade"
      transitionEnterTimeout={800}
      transitionLeaveTimeout={500}
      transitionAppear
      transitionAppearTimeout={500}
    >
      <div>
        You will die {yearsFromNow} years from now, on <strong>{props.dateOfDeath.format("dddd [the] Do [of] MMMM YYYY")}</strong>, at an age of <strong>{ageAtDeath}</strong>
      </div>
    </CSSTransitionGroup>
  );
}

export default Result;
