import React from 'react';
import PropTypes from 'prop-types';
import lifeExpectancies from '../api/lifeExpectancies';
import { CSSTransitionGroup } from 'react-transition-group';
import moment from 'moment';
import AddToCalendar from 'react-add-to-calendar';

function Result(props) {


  const ageAtDeath = props.dateOfDeath.diff(props.birthday, 'years');
  const yearsFromNow = props.dateOfDeath.diff(moment(),'years');
  const fiftyStart = props.dateOfDeath.clone().subtract(50,'year').format("YYYY-MM-DD[T12:00:00]Z")
  const fiftyEnd = props.dateOfDeath.clone().subtract(50,'year').format("YYYY-MM-DD[T12:15:00]Z")
  const fortyYearAnniversary = props.dateOfDeath.clone().subtract(40,'year')
  const thirtyYearAnniversary = props.dateOfDeath.clone().subtract(30,'year')
  const twentyYearAnniversary = props.dateOfDeath.clone().subtract(20,'year')
  const tenYearAnniversary = props.dateOfDeath.clone().subtract(10,'year')
  let icon = { 'calendar-plus-o': 'left' };
  var FiftyYearAnniversary = {
      title: "50 years until you will die",
      description: "Hope you are doing something meaningful today!",
      location: "Everywhere",
      startTime: fiftyStart,
      endTime: fiftyEnd
    };
  return (
    <CSSTransitionGroup
      className="container result"
      component="div"
      transitionName="fade"
      transitionEnterTimeout={800}
      transitionLeaveTimeout={500}
      transitionAppear
      transitionAppearTimeout={500}
    >
      <div>
        You will die in {yearsFromNow} years time on <strong>{props.dateOfDeath.format("dddd [the] Do [of] MMMM YYYY")}</strong>, at an age of <strong>{ageAtDeath}</strong> years
      </div>
      <div>
        <AddToCalendar event={FiftyYearAnniversary} buttonTemplate={icon} buttonLabel="Add 20 year death anniversary to my calendar"/>
      </div>
      <div>
        <AddToCalendar event={FiftyYearAnniversary} buttonTemplate={icon} buttonLabel="Add 20 year death anniversary to my calendar"/>
      </div>

    </CSSTransitionGroup>
  );
}

export default Result;
