import React from 'react';
import PropTypes from 'prop-types';
import lifeExpectancies from '../api/lifeExpectancies';
import { CSSTransitionGroup } from 'react-transition-group';
import moment from 'moment';
import AddToCalendar from 'react-add-to-calendar';

function Result(props) {


  const ageAtDeath = props.dateOfDeath.diff(props.birthday, 'years');
  const yearsFromNow = props.dateOfDeath.diff(moment(),'years');
  const fiftyStart = props.dateOfDeath.clone().subtract(50,'year').format("YYYY-MM-DD[T12:00:00]Z")
  const fiftyEnd = props.dateOfDeath.clone().subtract(50,'year').format("YYYY-MM-DD[T12:15:00]Z")
  const fortyStart = props.dateOfDeath.clone().subtract(40,'year').format("YYYY-MM-DD[T12:00:00]Z")
  const fortyEnd = props.dateOfDeath.clone().subtract(40,'year').format("YYYY-MM-DD[T12:15:00]Z")
  const thirtyStart = props.dateOfDeath.clone().subtract(30,'year').format("YYYY-MM-DD[T12:00:00]Z")
  const thirtyEnd = props.dateOfDeath.clone().subtract(30,'year').format("YYYY-MM-DD[T12:15:00]Z")
  const twentyStart = props.dateOfDeath.clone().subtract(20,'year').format("YYYY-MM-DD[T12:00:00]Z")
  const twentyEnd = props.dateOfDeath.clone().subtract(20,'year').format("YYYY-MM-DD[T12:15:00]Z") 
  const tenStart = props.dateOfDeath.clone().subtract(10,'year').format("YYYY-MM-DD[T12:00:00]Z")
  const tenEnd = props.dateOfDeath.clone().subtract(10,'year').format("YYYY-MM-DD[T12:15:00]Z")

  let icon = { 'calendar-plus-o': 'left' };
  var fiftyYearAnniversary = {
    title: "50 years until you will die",
    description: "Hope you are doing something meaningful today!",
    location: "Everywhere",
    startTime: fiftyStart,
    endTime: fiftyEnd
  };
  var fortyYearAnniversary = {
    title: "40 years until you will die",
    description: "Hope you are doing something meaningful today!",
    location: "Everywhere",
    startTime: fortyStart,
    endTime: fortyEnd
  };
  var thirtyYearAnniversary = {
    title: "30 years until you will die",
    description: "Hope you are doing something meaningful today!",
    location: "Everywhere",
    startTime: thirtyStart,
    endTime: thirtyEnd
  };
  return (
    <CSSTransitionGroup
      className="container result"
      component="div"
      transitionName="fade"
      transitionEnterTimeout={800}
      transitionLeaveTimeout={500}
      transitionAppear
      transitionAppearTimeout={500}
    >
      <div>
        You will die in {yearsFromNow} years time on <strong>{props.dateOfDeath.format("dddd [the] Do [of] MMMM YYYY")}</strong>, at an age of <strong>{ageAtDeath}</strong> years
      </div>
      <div>
        <AddToCalendar event={fiftyYearAnniversary} buttonTemplate={icon} buttonLabel="Add 50 year death anniversary to my calendar"/>
      </div>
      <div>
        <AddToCalendar event={fortyYearAnniversary} buttonTemplate={icon} buttonLabel="Add 40 year death anniversary to my calendar"/>
      </div>
      <div>
        <AddToCalendar event={thirtyYearAnniversary} buttonTemplate={icon} buttonLabel="Add 30 year death anniversary to my calendar"/>
      </div>
      <div>
        <AddToCalendar event={twentyYearAnniversary} buttonTemplate={icon} buttonLabel="Add 20 year death anniversary to my calendar"/>
      </div>
      <div>
        <AddToCalendar event={tenYearAnniversary} buttonTemplate={icon} buttonLabel="Add 10 year death anniversary to my calendar"/>
      </div>

    </CSSTransitionGroup>
  );
}

export default Result;
